import React, {useEffect, useState} from 'react';
import axios from "axios";
import history from "./history";
import Jar from "./Components/Jar";
import Button from "./Components/Buttons";
import Wrapper from "./Components/Wrapper";
import Message from "./Components/Message";
import Title from "./Components/Title";
import {get} from "lodash-es";

const getUsersJars = () => {
    const username = window.localStorage.getItem('user');
    return axios
        .get('http://localhost:8080/api/jars/' + username)
        .then((jars) => {
            console.log(jars.data);
            return jars.data;
        }).catch((err) => {
            console.log(err);
        });
};

const createJar = (name) => {
    const username = window.localStorage.getItem('user');
    return axios
        .post('localhost:8080/api/jars/create/' + username, {
            name: name,
        })
        .then((response) => {
            console.log(response);
        }).catch((err) => {
            console.log(err);
        });
};



const logOut = (event) => {
    const username = window.localStorage.getItem('user');
    event.preventDefault();
    axios.delete('http://localhost:8080/api/logins/' + username
    ).then((response) => {
        console.log(response);
        history.push("/login");
    }).catch((err) => {
        console.log(err);
    });
};
const getJarName = (jarInfo) => {
    const {name} = jarInfo;
    return `${name}`
};

const getJarAmount = (jarInfo) => {
    const {amount} = jarInfo;
    return `${amount}`
};

const handleChange = (setField) => (event) => {
    setField(event.target.value);
};

function Dashboard() {
    const [jarInfos, setJarInfos] = useState([]);
    const [name, setName] = useState('');


    useEffect(() => {
        getUsersJars().then((jarData) => {
            setJarInfos(jarData);
        }).catch((err) => {
            console.log(err);
        });
    }, []);


    return (
        <Wrapper>
            <Title main = {"Money Jars"}/>
            <Message message={""} />
            {jarInfos.map((jarInfo, id) => (
                <div key ={id}>
                <Jar
                    Name = {getJarName(jarInfo)}
                    Amount = {getJarAmount(jarInfo)}
                />
                </div>
            ))}

            <Message message={""} />
            <form>
                <label htmlFor="name">
                    Jar Name
                    &nbsp;&nbsp;
                    <input
                        value={name}
                        onChange={handleChange(setName)}
                        name="email"
                        type="email"
                    />
                </label>
                    <Button type ="button"  buttonStyle="btn--warning--solid" buttonSize="btn-small">Create Jar</Button>
            </form>

            <Button type ="button"  buttonStyle="btn--solid--solid" buttonSize="btn-small" onClick={logOut}>log Out</Button>
        </Wrapper>
    )
}
export default Dashboard;
