import React, {useEffect, useState} from 'react';
import axios from "axios";
import history from "./history";
import Button from "./Components/Buttons";
import Wrapper from "./Components/Wrapper";
import Message from "./Components/Message";
import Title from "./Components/Title";
import OpenJar from "./Components/OpenJar";
import {get} from "lodash-es";
import Scrollbar from "react-scrollbars-custom";



const jarInfo = JSON.parse(localStorage.getItem('jar'));
const username = localStorage.getItem('user');
const deleteJar = (event) => {
    event.preventDefault();
    axios.delete('http://localhost:8080/api/jars/' + username + '/' + jarInfo.id
    ).then((response) => {
        history.push("/dashboard");
        localStorage.removeItem('jar');
        window.location.reload();
    }).catch((err) => {
        console.log(err);
    });
};

function JarContents() {
    const [donation, setDonation] = useState('');
    const [contributor, setContributor] = useState('');
    const [rename, setRename] = useState('');
    const [shareError, setShareError] = useState(null);
    const [shareSuccess, setShareSuccess] = useState(null);
    const donate = (event) => {
        event.preventDefault();
        const username = window.localStorage.getItem('user');
        return axios.put('http://localhost:8080/api/jars/donate/' + username + "/" + jarInfo.id,  donation, {
            "headers": {
                "content-type": "application/json",
            },
        }
        ).then((response) => {
            jarInfo.amount = parseInt(jarInfo.amount) + parseInt(donation);
            localStorage.setItem('jar', JSON.stringify(jarInfo));
            window.location.reload();
            console.log(response);
        }).catch((err) => {
            console.log(err);
        });
    };

    const getDonations = () => {
        const username = window.localStorage.getItem('user');
        return axios
            .get('http://localhost:8080/api/jars/' + username)
            .then((jars) => {
                console.log(jars.data);
                return jars.data;
            }).catch((err) => {
                console.log(err);
            });
    };


    const shareJar = (event) => {
        event.preventDefault();
        return axios.post('http://localhost:8080/api/jars/share/' + jarInfo.id, contributor, {
                "headers": {
                    "content-type": "application/json",
                },
            }
        ).then((response) => {
            console.log(response);
            const message = get(
                shareSuccess,
                'response.data.error_description',
                'Successfully shared!',
            );
            setShareSuccess(message);
        }).catch((shareError) => {
            setShareError(shareError.response.data.message);
        });
    };


    const renameJar = (event) => {
        event.preventDefault();
        return axios.put('http://localhost:8080/api/jars/rename/' + jarInfo.id, rename, {
                "headers": {
                    "content-type": "application/json",
                },
            }
        ).then((response) => {
            jarInfo.name = rename;
            localStorage.setItem('jar', JSON.stringify(jarInfo));
            window.location.reload();
            console.log(response);
        }).catch((err) => {
            console.log(err);
        });
    };

    const handleChange = (setField) => (event) => {
        setField(event.target.value);
    };

    return (
        <Wrapper>
            <div  style= {{position: 'relative', left: -650}}>
            <Button type ="button"  buttonStyle="btn--play--solid" buttonSize="btn-small"
                    onClick={() =>  {
                        localStorage.removeItem('jar');
                        history.push("/dashboard");
                        window.location.reload();
                    }}>Back to Jars</Button>
            </div>
            <Title main = {jarInfo.name + ' Jar'}/>
            <OpenJar
                Name = {jarInfo.name}
                Amount = {jarInfo.amount}
            />
            <Scrollbar style={{ width: 250, height: 250 }}>
                <p>Hello world!</p>
            </Scrollbar>
            <Message message={""} />
            <div>
                <Button type ="button"  buttonStyle="btn--warning--solid" buttonSize="btn-small" onClick={donate}>
                    Donate
                </Button>
                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <input
                    value = {donation}
                    onChange={handleChange(setDonation)}
                    type= "number"
                />
            </div>
            <Message message={""} />
            <div>
                <Button type ="button"  buttonStyle="btn--team--solid" buttonSize="btn-small" onClick={shareJar}>
                    Share
                </Button>
                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <input
                    value = {contributor}
                    onChange={handleChange(setContributor)}
                    type="text"/>
                    {shareError && <p style={{color: 'red'}}>{shareError}</p>}
                    {shareSuccess && <p style={{color: 'green'}}>{shareSuccess}</p>}
            </div>
            <Message message={""} />
            {jarInfo.creator === username &&
            <div>
                <Button type ="button"  buttonStyle="btn--primary--solid" buttonSize="btn-small" onClick={renameJar}>
                    Rename
                </Button>
                &nbsp;&nbsp;&nbsp;
                <input
                    value = {rename}
                    onChange={handleChange(setRename)}
                    type= "text"
                />
            </div>}
            <Message message={""} />
            {jarInfo.creator === username && <Button type ="button"  buttonStyle="btn--danger--solid" buttonSize="btn-small" onClick={deleteJar}>Delete</Button>}
        </Wrapper>
    )
}

export default JarContents;
