import React, {Component, useEffect, useState} from 'react';
import axios from "axios";
import history from "./history";
import Jar from "./Components/Jar";
import Button from "./Components/Buttons";
import Wrapper from "./Components/Wrapper";
import Message from "./Components/Message";
import App from "./App";


const logOut = (event) => {
    event.preventDefault();
    axios.delete('http://localhost:8080/api/logins/' + window.globalUsername
    ).then((response) => {
        console.log(response);
        history.push("/login");
    }).catch((err) => {
        console.log(err);
    });
};

const getUsersJars = () => {
    return axios
        .get('http://localhost:8080/api/jars/' + window.globalUsername)
        .then((jars) => {
            console.log(jars.data);
            return jars.data;
        }).catch((err) => {
            console.log(err);
        });
};

const getJarName = (jarInfo) => {
    const {name} = jarInfo;
    return `${name}`
};

const getJarAmount = (jarInfo) => {
    const {amount} = jarInfo;
    return `${amount}`
};

function Dashboard() {
    const [jarInfos, setJarInfos] = useState([]);

    useEffect(() => {
        getUsersJars().then((jarData) => {
            setJarInfos(jarData);
            console.log(jarData);
        }).catch((err) => {
            console.log(err);
        });
    }, []);


    return (
        <Wrapper>
            <Message message={""} />
            {jarInfos.map((jarInfo, id) => (
                <div key ={id}>
                <Jar
                    Name = {getJarName(jarInfo)}
                    Amount = {getJarAmount(jarInfo)}
                />
                </div>
            ))}


            <Message message={""} />
            <Button type ="button"  buttonStyle="btn--danger--solid" buttonSize="btn-small">Create Jar</Button>
            <Button type ="button"  buttonStyle="btn--solid--solid" buttonSize="btn-small" onClick={logOut}>log Out</Button>
        </Wrapper>
    )
}
export default Dashboard;
