import React, {useEffect, useState} from 'react';
import axios from "axios";
import history from "./history";
import Jar from "./Components/Jar";
import Button from "./Components/Buttons";
import Wrapper from "./Components/Wrapper";
import Message from "./Components/Message";
import Title from "./Components/Title";


const getUsersJars = () => {
    const username = window.localStorage.getItem('user');
    return axios
        .get('http://localhost:8080/api/jars/' + username)
        .then((jars) => {
            console.log(jars.data);
            return jars.data;
        }).catch((err) => {
            console.log(err);
        });
};

const createJar = (event) => {
    event.preventDefault();
    const username = window.localStorage.getItem('user');
    return axios
        .post('http://localhost:8080/api/jars/create/' + username,
            'untitled', {
                "headers": {
                    "content-type": "application/json",
                },
            }
        )
        .then((response) => {
            window.location.reload();
            console.log(response);
        }).catch((err) => {
            console.log(err);
        });
};

const logOut = (event) => {
    event.preventDefault();
    const username = window.localStorage.getItem('user');
    axios.delete('http://localhost:8080/api/logins/' + username
    ).then((response) => {
        console.log(response);
        history.push("/login");
    }).catch((err) => {
        console.log(err);
    });
};
const getJarName = (jarInfo) => {
    const {name} = jarInfo;
    return `${name}`
};

const getJarAmount = (jarInfo) => {
    const {amount} = jarInfo;
    return `${amount}`
};



function Dashboard() {
    const [jarInfos, setJarInfos] = useState([]);

    useEffect(() => {
        getUsersJars().then((jarData) => {
            setJarInfos(jarData);
        }).catch((err) => {
            console.log(err);
        });
    }, []);


    return (
        <Wrapper>
            <Title main = {"Money Jars"}/>
            <Message message={""} />
            {jarInfos.map((jarInfo, id) => (
                <div key={id} style={{margin: auto}}>
                <Jar
                    Name = {getJarName(jarInfo)}
                    Amount = {getJarAmount(jarInfo)}
                    onClick={() => {
                        const jar =  JSON.stringify(jarInfo);
                        localStorage.setItem('jar', jar);
                        history.push("/openJar");
                        window.location.reload();
                    }}
                />
                </div>
            ))}

            <Message message={""} />
            <Button type ="button"  buttonStyle="btn--warning--solid" buttonSize="btn-small" onClick={createJar}>Create Jar</Button>
            <Button type ="button"  buttonStyle="btn--solid--solid" buttonSize="btn-small" onClick={logOut}>log Out</Button>
        </Wrapper>
    )
}
export default Dashboard;
