import React, {useEffect, useState} from 'react';
import axios from "axios";
import history from "./history";
import Jar from "./Components/Jar";
import Button from "./Components/Buttons";
import Wrapper from "./Components/Wrapper";
import Message from "./Components/Message";
import Title from "./Components/Title";
import OpenJar from "./Components/OpenJar";


const jarInfo = JSON.parse(localStorage.getItem('jar'));

const deleteJar = (event) => {
    event.preventDefault();
    const username = localStorage.getItem('user');
    axios.delete('http://localhost:8080/api/jars/' + username + '/' + jarInfo.id
    ).then((response) => {
        history.push("/dashboard");
        localStorage.removeItem('jar');
        window.location.reload();
    }).catch((err) => {
        console.log(err);
    });
};

function JarContents() {
    const [donation, setDonation] = useState(0);
    const [contributor, setContributor] = useState('');
    const [rename, setRename] = useState('');

    const donate = (event) => {
        event.preventDefault();
        const username = window.localStorage.getItem('user');
        axios.put('http://localhost:8080/api/jars/donate/' + username + "/" + jarInfo.id, {
                donation: donation
            }
        ).then((response) => {
            window.location.reload();
        }).catch((err) => {
            console.log(err);
        });
    };


    const shareJar = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8080/api/jars/share/' + jarInfo.id, {
                contributor: contributor
            }
        ).then((response) => {
            console.log(response);
        }).catch((err) => {
            console.log(err);
        });
    };

    const renameJar = (event) => {
        event.preventDefault();
        axios.put('http://localhost:8080/api/jars/rename' + jarInfo.id, {
                rename: rename
            }
        ).then((response) => {
            console.log(response);
        }).catch((err) => {
            console.log(err);
        });
    };

    const handleChange = (setField) => (event) => {
        setField(event.target.value);
    };

    return (
        <Wrapper>
            <Title main = {jarInfo.name + ' Jar'}/>
            <OpenJar
                Name = {jarInfo.name}
                Amount = {jarInfo.amount}
            />
            <Message message={""} />
            <Button type ="button"  buttonStyle="btn--warning--solid" buttonSize="btn-small" onClick={donate}>
                Donate
                &nbsp;&nbsp;
                <input
                    value = {donation}
                    onChange={handleChange(setDonation)}
                    name = "donation"
                    type = "donation"
                />

            </Button>

            <Message message={""} />
            <Button type ="button"  buttonStyle="btn--team--solid" buttonSize="btn-small"  >Share</Button>
            <input/>
            <Message message={""} />
            <Button type ="button"  buttonStyle="btn--primary--solid" buttonSize="btn-small"  >Rename</Button>
            <input/>
            <Message message={""} />
            <Button type ="button"  buttonStyle="btn--play--solid" buttonSize="btn-small" onClick={() =>  {
                localStorage.removeItem('jar');
                history.push("/dashboard");
                window.location.reload();
            }}>Back to Jars</Button>
            <Button type ="button"  buttonStyle="btn--danger--solid" buttonSize="btn-small" onClick={deleteJar}>Delete</Button>
        </Wrapper>
    )
}

export default JarContents;
