import axios from 'axios';
const api = axios.create({
    baseURL: 'http://localhost:8080/api/',
    withCredentials: true,
    headers: {
        'Content-Type': 'application/vnd.api+json',
        "Access-Control-Allow-Origin": "*",
    },
});
export default api;
